import { once } from "../util/once";
export function setupActions(...createdActions) {
    const myActions = once(() => {
        const actions = {};
        for (const action of createdActions) {
            actions[action.myMetaData.id] = action;
        }
        return actions;
    });
    return {
        get actions() {
            return myActions();
        },
        get actionsMetadata() {
            return createdActions.map((x) => x.myMetaData);
        },
    };
}
