import { isValueSpecListOf } from "../config/configTypes";
import { Config } from "../config/builder/config";
import { List } from "../config/builder/list";
import { Value } from "../config/builder/value";
describe("Config Types", () => {
    test("isValueSpecListOf", async () => {
        const options = [List.obj, List.text, List.number];
        for (const option of options) {
            const test = option({}, { spec: Config.of({}) });
            const someList = await Value.list(test).build({});
            if (isValueSpecListOf(someList, "text")) {
                someList.spec;
            }
            else if (isValueSpecListOf(someList, "number")) {
                someList.spec;
            }
            else if (isValueSpecListOf(someList, "object")) {
                someList.spec;
            }
            else {
                throw new Error("Failed to figure out the type: " + JSON.stringify(someList));
            }
        }
    });
});
